@charset "UTF-8";
* {
  box-sizing: border-box;
  color: #ffffff;
  font-family: "Montserrat";
}

body {
  display: flex;
  flex-direction: column;
  justify-content: space-between;
  background-color: #000000;
}

.container {
  max-width: 1440px;
  margin: 0 auto;
}

img {
  max-width: 100%;
}

main {
  margin-top: 30px;
}

.section_title {
  margin: 20px 0 20px 70px;
  font-size: 30px;
  color: #ffffff;
}

.film-cards-gallery {
  position: relative;
  max-width: 1440px;
  padding: 40px 0;
  margin: 0 auto;
  background-color: #0a031c;
}

.gallery-wrapper {
  width: 90%;
  margin: 0px auto;
  padding: 20px 0;
  overflow-x: auto;
}

.gallery-wrapper::-webkit-scrollbar {
  display: none;
}

.movies-list, .series-list, .cartoon-list {
  display: flex;
  flex-wrap: nowrap;
  width: 1400px;
  list-style-type: none;
  margin: 0;
  padding: 0;
}

.movies-list__item {
  align-items: flex-start;
  margin: 0 20px;
  padding: 0;
  min-width: 200px;
  border-radius: 12px;
  transition: transform 0.4s;
}

.movies-list__item:hover {
  transform: scale(105%);
  box-shadow: 0px 0px 10px 10px rgba(255, 255, 255, 0.3);
}

.movies-list__link {
  display: inline-block;
}

.movies-list__img {
  height: 100%;
  border-radius: 12px;
  margin: auto 0;
  padding: 0;
}

.movies-gallery,
.series-gallery,
.cartoon-container {
  position: relative;
}

.gallery-btn--right, .gallery-btn--left {
  position: absolute;
  top: 250px;
  margin: 0;
  padding: 0;
  width: 37px;
  height: 48px;
  border-radius: 5px;
  cursor: pointer;
  background-color: rgba(0, 0, 0, 0.3);
  border: none;
}
.gallery-btn--right img, .gallery-btn--left img {
  width: 37px;
  height: 48px;
}

.gallery-btn--right {
  right: 80px;
}

.gallery-btn--left {
  left: 80px;
  transform: rotate(180deg);
}

.gallery-btn--right:hover {
  background-color: rgba(0, 0, 0, 0.9);
}

.gallery-btn--left:hover {
  background-color: rgba(0, 0, 0, 0.9);
}

.modal-window {
  display: none;
}

.modal-window--active {
  display: inline-block;
  position: absolute;
  left: 10px;
  top: 1%;
  width: 95%;
  margin: auto;
  z-index: 99;
  position: fixed;
}

.modal-window__container {
  position: relative;
  display: flex;
  justify-content: space-between;
  max-width: 1364px;
  margin: 0 auto;
  padding: 40px;
  border-radius: 20px;
  background-color: #000000;
}

.modal-window__container::after {
  content: "";
  top: -15px;
  left: -20px;
  position: absolute;
  height: 105%;
  width: 105%;
  background: linear-gradient(90deg, #FF4D6D, #54E1FF);
  z-index: -1;
  filter: blur(40px);
}

.modal-window__title {
  margin: 10px 0;
  font-size: 70px;
  font-weight: 900;
}

.modal-window__year,
.modal-window__country,
.modal-window__genres {
  margin: 15px 0 0 0;
  font-size: 22px;
}

.modal-window__img-box {
  min-width: 40%;
}
.modal-window__img-box img {
  width: 100%;
  border-radius: 20px;
}

.modal-window__description {
  width: 60%;
  margin: 20px 0 0 40px;
}

.modal-window__text {
  font-size: 20px;
  font-weight: 500;
  line-height: 30px;
}

.modal-window__btn {
  display: inline-block;
  position: absolute;
  top: 25px;
  right: 25px;
  width: 35px;
  height: 35px;
  border: none;
  border-radius: 5px;
  background-color: rgba(71, 71, 71, 0);
}
.modal-window__btn img {
  width: 30px;
  height: 30px;
}

@media screen and (max-width: 1043px) and (min-width: 768px) {
  .gallery-btn--right, .gallery-btn--left {
    display: none;
  }
  .modal-window__title {
    font-size: 50px;
  }
  .modal-window__text {
    font-size: 16px;
    padding: 10px;
  }
  .modal-window__description {
    width: 100%;
    margin-left: 20px;
  }
  .modal-window__text {
    width: 100%;
  }
}
@media screen and (max-width: 767px) {
  .gallery-btn--right, .gallery-btn--left {
    display: none;
  }
  .modal-window__container {
    flex-direction: column;
  }
  .modal-window__img-box {
    width: 100%;
  }
  .modal-window__description {
    margin: 0;
    padding: 0;
    width: 100%;
  }
  .modal-window__title {
    font-size: 50px;
  }
  .modal-window__text {
    width: 100%;
    font-size: 16px;
  }
}
.search-result {
  display: flex;
  flex-wrap: wrap;
  margin: 10px auto;
}

/* //slider styles// */
swiper-container {
  width: 100%;
  padding-top: 50px;
  padding-bottom: 50px;
  margin-top: 30px;
}

swiper-slide {
  background-position: center;
  background-size: cover;
  width: 25%;
}

swiper-slide img {
  display: block;
  width: 100%;
  border-radius: 20px;
}

/* header styles */
.header__container {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 16px;
  height: 60px;
  flex-wrap: wrap;
  margin-bottom: 10px;
}

.header__content {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 16px;
  height: 60px;
}

.header__left-part {
  display: flex;
}

.header__logo {
  font-size: 32px;
  text-decoration: none;
  color: aliceblue;
  width: 50px;
}

.header__search {
  padding: 8px;
  border: 2px solid #000000;
  border-radius: 5px;
  outline: none;
  color: #000000;
}

.header__search::-moz-placeholder {
  color: #999;
}

.header__search::placeholder {
  color: #999;
}

.header__list {
  font-size: 18px;
  list-style-type: none;
  display: flex;
  flex-wrap: wrap;
  gap: 20px;
}

.footer__list {
  list-style-type: none;
  display: flex;
  gap: 15px;
  justify-content: center;
  flex-wrap: wrap;
}

.footer {
  margin-top: 10px;
  border-top: 1px solid aliceblue;
}

a {
  text-decoration: none;
}

a:hover {
  color: #FF4D6D;
}

.creators {
  text-align: center;
  font-size: 10px;
  color: #FF4D6D;
}

@media (max-width: 850px) {
  .header__right-part {
    display: flex;
    justify-content: center;
    align-self: center;
    margin: 0 auto;
    margin-bottom: 20px;
  }
}
@media (max-width: 550px) {
  .header__list {
    font-size: 12px;
  }
  .footer__list {
    font-size: 12px;
  }
}
/* header end */
.popular-movies__container {
  max-width: 1440px;
  margin: 0 auto;
  padding: 40px 0;
  position: relative;
}

.popular-movies {
  display: flex;
  flex-wrap: wrap;
  justify-content: space-around;
  margin: 50px auto;
  max-width: 1270px;
  text-align: center;
}

.popular-movie {
  width: 240px;
  margin: 10px;
  position: relative;
}

.popular-movie:hover {
  transform: scale(105%);
  box-shadow: 0px 0px 10px 10px #263459;
}

.search-movie_cover-inner,
.popular-movie__cover-inner {
  position: relative;
  height: 360px;
  box-shadow: 10px 5px 5px #263459;
}

.search-movie__cover,
.popular-movie__cover {
  max-width: 100%;
  height: 100%;
}

.popular-movie__cover--darkened {
  background-color: #000000;
  opacity: 0.1;
  position: absolute;
  top: 0;
  left: 0;
  right: 0;
  max-width: 100%;
  height: 100%;
  z-index: 1;
}

.popular-movie__cover--darkened:hover {
  background-color: #0a031c;
  cursor: pointer;
}

.search-movie__info,
.popular-movie__info {
  padding: 10px 0px;
  text-align: center;
}

.search-movie__title,
.popular-movie__title {
  font-family: "Montserrat";
  text-align: center;
  font-size: 18px;
  color: white;
}

.search-movie__info {
  margin-bottom: 20px;
}

.search-movie__category,
.popular-movie__category {
  font-family: "Montserrat";
  font-size: 14px;
  color: rgb(153, 150, 150);
  font-style: italic;
  margin-bottom: 3px;
}

.popular-movie__average {
  position: absolute;
  top: 10px;
  left: 10px;
  border-radius: 50%;
  width: 36px;
  height: 36px;
  display: flex;
  justify-content: center;
  align-items: center;
  background-color: #0a031c;
  color: white;
  font-size: 14px;
}

.popular-movie__average--green {
  border: 1px solid green;
}

.popular-movie__average--orange {
  border: 1px solid orange;
}

.popular-movie__average--red {
  border: 1px solid red;
}

.modal-overlay {
  display: none;
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-color: rgba(0, 0, 0, 0.8);
  align-items: center;
